import React from 'react'
import './intro.css';
import btnImg from "../../assets/hireme_logo.png" ;
import avatar from "../../assets/rinz.png";

import {Link} from "react-scroll";

const Intro = () => {
  return (
     <section className='intro'>
        <div className='introcontent'>
            <span className='hello'>Hello,</span>
            <span className='introText'>I'm <span className='introName'>RINSA S</span><br />Website Designer</span>
            <p className='intoPara'>I'm a skilled Web Designer in creating visually appealing and user friendly websites</p>
            <Link><button className='btn'><img src={btnImg} alt='hire me' className='btnImg'></img>Hire Me</button></Link>
        </div>
        <img src={avatar} alt='profile' className></img>
     </section>
     
  )
}

export default Intro;
